const express=require("express"),twilio=require("twilio"),app=express(),cors=require("cors");require("dotenv").config();const AccessToken=twilio.jwt.AccessToken,ChatGrant=AccessToken.ChatGrant;function TokenGenerator(identity){const appName="TwilioChat",endpoint=appName+":"+identity,chatGrant=new ChatGrant({serviceSid:process.env.TWILIO_CHAT_SERVICE_SID,endpointId:endpoint}),token=new AccessToken(process.env.TWILIO_ACCOUNT_SID,process.env.TWILIO_API_KEY,process.env.TWILIO_API_SECRET);return token.addGrant(chatGrant),token.identity=identity,console.log("tokennnn",token),token}app.use(cors()),app.get("/",(req,res)=>{console.log(process.env,"=====env"),res.send("hey")}),app.post("/get-token/:id",(req,res)=>{console.log(process.env.TWILIO_CHAT_SERVICE_SID,"env");const token=TokenGenerator(req.params.id);res.send({identity:token.identity,jwt:token.toJwt()})}),app.listen(3001,()=>console.log("listening 3001"));